mapper.insert:
insert into message (
	message_no, sender_no, receiver_no, content, type, url
)
values (
	#{messageNo}, #{senderNo}, #{receiverNo}, #{content}, #{type}, #{url}
)

mapper.update:
update message set
	sender_deleted = 'Y'
where message_no = #{messageNo}

update message set
	receiver_deleted = 'Y'
where message_no = #{messageNo}

mapper.select:
select message_no, sender_no, receiver_no, content, type, sent_time as sentTime, read_time as readTime, is_read as isRead, sender_deleted as senderDeleted, receiver_deleted as receiverDeleted, url as url from message where receiver_no = #{memberNo} and receiver_deleted = 'N' and type in
#{type}
order by message_no desc
